import React, { Component } from "react";
import { Link } from "react-router-dom";
import api from "../../services/api";
import "./sign-up.css";

class SignUp extends Component {
  state = {
      first_name: '',
      email: '',
      password: ''
  };

  handleSignUp = async e => {
    e.preventDefault();
    const { first_name, email, password } = this.state;
    if (!first_name || !email || !password) {
      this.setState({ error: "Preencha todos os dados para se cadastrar." });
    } else {
      try {
        await api.post("/users/register", { first_name, email, password });
        this.props.history.push("/");
      } catch (err) {
        console.log(err);
        this.setState({ error: "Ocorreu um erro. Tente novamente." });
      }
    }
  };

  render() {
    return (
      <div id="sign-up" className="small-container">
        <h3>Sign up</h3>
        <div className="box-group">
          <form onSubmit={this.handleSignUp}>
            <div>
              {this.state.error && <p>{this.state.error}</p>}
              <input
                type="text"
                placeholder="Nome de usuário"
                onChange={e => this.setState({ first_name: e.target.value })}
              />
              <input
                type="email"
                placeholder="Endereço de e-mail"
                onChange={e => this.setState({ email: e.target.value })}
              />
              <input
                type="password"
                placeholder="Senha"
                onChange={e => this.setState({ password: e.target.value })}
              />
              <button id="inp-btn" type="submit">Cadastrar</button>
              <hr />
              <div id="btn-login">
                <Link to="/login">Fazer login</Link>
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default SignUp;
