import React, { Component } from "react";
import { Link } from "react-router-dom";
import api from "../../services/api";
import { history } from "../../history";
import "./sign-in.css";

const SignIn = () => {
  const handleSubmit = values => {
    api.post('http://localhost:8080/v1/api/auth', values)
        .then(resp => {
            const { data } = resp
            if (data) {
                localStorage.setItem('app-token', data)
                history.push('/')
            }
        })
}


    return (
      <div id="sign-up" className="small-container">
        <h3>Sign in</h3>
        <div className="box-group">
          <form 
                          initialValues={{}}
                          onSubmit={handleSubmit}
>
            <input
              type="email"
              placeholder="EndereÃ§o de e-mail"
              onChange={e => this.setState({ person_email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Senha"
              onChange={e => this.setState({ person_password: e.target.value })}
            />
            <button id="inp-btn" type="submit">Entrar</button>
            <hr />
            <div id="btn-login">
              <Link to="/signup">Criar conta</Link>
            </div>
          </form>
        </div>
      </div>
    );

}

export default SignIn;
