import React, { Component } from "react";
import { Link } from "react-router-dom";
import api from "../../services/api";
import { login } from "../../services/auth";
import "./sign-in.css";

class SignIn extends Component {
  state = {
    email: '',
    password: ''
  };

  handleSignIn = async e => {
    e.preventDefault();
    const { person_email, person_password } = this.state;
    if (!person_email || !person_password) {
      this.setState({ error: "Preencha e-mail e senha para continuar!" });
    } else {
      try {
        const response = await api.post("/users/login", { person_email, person_password });
        login(response.data.token);
        this.props.history.push("/");
      } catch (err) {
        this.setState({
          error:
            "Houve um problema com o login. "
        });
      }
    }
  };

  render() {
    return (
      <div id="sign-up" className="small-container">
        <h3>Sign in</h3>
        <div className="box-group">
          <form onSubmit={this.handleSignIn}>
            {this.state.error && <p>{this.state.error}</p>}
            <input
              type="email"
              placeholder="EndereÃ§o de e-mail"
              onChange={e => this.setState({ person_email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Senha"
              onChange={e => this.setState({ person_password: e.target.value })}
            />
            <button id="inp-btn" type="submit">Entrar</button>
            <hr />
            <div id="btn-login">
              <Link to="/signup">Criar conta</Link>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default SignIn;
